

public class CheckMaxMinTwoNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10;
		int num2=20;
		if(num1>num2) {
			System.out.println(num1+" num1 is Maximum ");
			System.out.println(num2+" num2 is Minimum");
		}else {
			System.out.println(num2+" num2 is Maximun");
			System.out.println(num1+" num1 is Minimum");

		}

	}

}
